(function($){$.fn.wideText=function(){return this.each(function(){var obj=$(this),rtext=obj.addClass("wtext");$(window).on("resize",function(){obj.css({"fontSize":parseInt(obj.css("fontSize"))*(obj.parent().width()/obj.width())+"px","visibility":"visible"})}).resize()})}
$('.widetext').wideText();})(jQuery);